package main

import (
	"context"
	"nugg-webauthn/core/pkg/dynamo"
	"nugg-webauthn/core/pkg/hex"
	"reflect"
	"testing"

	"github.com/aws/aws-sdk-go-v2/service/dynamodb"
	dtypes "github.com/aws/aws-sdk-go-v2/service/dynamodb/types"
	"github.com/rs/zerolog"
)

func DummyHandler(t *testing.T) *Handler {

	dynamoClient := dynamo.NewMockClient(t)

	return &Handler{
		Id:     "test",
		Ctx:    context.Background(),
		Config: nil,
		Dynamo: dynamoClient,
		logger: zerolog.New(zerolog.NewConsoleWriter()).With().Caller().Timestamp().Logger(),
	}

}

func TestHandler_Invoke(t *testing.T) {

	h := DummyHandler(t)

	tests := []struct {
		name    string
		args    Input
		puts    []dtypes.Put
		want    Output
		wantErr bool
	}{
		{
			name: "B - body2",
			args: Input{
				Headers: map[string]string{
					"x-nugg-hex-attestation-key":  "0xfb1fd0ac98dca2891761baf97a486c75726900d3a94105afa598575f89c47295",
					"x-nugg-hex-session-id":       "0x3a298ca21194c5ee7920d2ffc5247d6fa0f330a038cf3933e138602660430b8d",
					"x-nugg-utf-client-data-json": "{\"challenge\":\"CcB74F8PT10Lordu5qtl4vFuQfOpRGhQZmCO9Z3cj48\",\"origin\":\"https://nugg.xyz\",\"type\":\"webauthn.create\"}",
				},
				Body: body2,
			},
			puts: []dtypes.Put{
				{
					Item: map[string]dtypes.AttributeValue{
						"challenge_id": &dtypes.AttributeValueMemberS{
							Value: (hex.MustBase64ToHash("CcB74F8PT10Lordu5qtl4vFuQfOpRGhQZmCO9Z3cj48").Hex()),
						},
						"session_id": &dtypes.AttributeValueMemberS{
							Value: "0x3a298ca21194c5ee7920d2ffc5247d6fa0f330a038cf3933e138602660430b8d",
						},
						"credential_id": &dtypes.AttributeValueMemberS{
							Value: "0x",
						},
						"ceremony_type": &dtypes.AttributeValueMemberS{
							Value: "webauthn.create",
						},
						"created_at": &dtypes.AttributeValueMemberN{
							Value: "1668984054",
						},
						"ttl": &dtypes.AttributeValueMemberN{
							Value: "1668984354",
						},
					},
					TableName: &h.Dynamo.CeremonyTableName,
				},
			},
			want: Output{
				StatusCode: 204,
				Headers: map[string]string{
					"Content-Length": "0",
				},
			},
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {

			// write all of the puts to dynamo
			for _, put := range tt.puts {

				_, err := h.Dynamo.PutItem(h.Ctx, &dynamodb.PutItemInput{
					Item:      put.Item,
					TableName: put.TableName,
				})
				if err != nil {
					t.Errorf("handler.Dynamo.PutItem() error = %v", err)
					return
				}
			}

			got, err := h.Invoke(h.Ctx, tt.args)
			if (err != nil) != tt.wantErr {
				t.Errorf("Handler.Invoke() error = %v, wantErr %v", err, tt.wantErr)
				return
			}
			if !reflect.DeepEqual(got, tt.want) {
				t.Errorf("Handler.Invoke() = %v, want %v", got, tt.want)
			}
		})
	}
}

var body2 = ""
